/**
 *  Imports
 */
import { IModel } from './models/IModel';
import { Document } from 'mongoose';
import { IError } from 'be-utils';
export interface IRepository<T extends Document> {
    /**
     *  Class properties
     */
    insert(model: IModel<T>, callback: (err: IError, model?: T) => void): any;
    update(id: string, model: IModel<T>, callback: (err: IError, model?: T) => void): any;
    updateByCondition(cond: any, model: IModel<T>, callback: (err: IError, model?: T) => void): any;
    updateBulk(keys: Array<string>, models: Array<IModel<T>>, callback: (err: IError) => void): any;
    findOne(cond: Object, callback: (err: IError, model?: T) => void): any;
}
